import { fetchSpotifyJSON, fetchWithRetry } from '@/lib/spotify-utils'
import { NextRequest, NextResponse } from 'next/server'
import { getServerSession } from 'next-auth'
import { getTopTracks } from '@/lib/database'
import { storeUserTokens } from '@/lib/spotify-tokens'
import { authOptions } from '@/lib/auth'

interface SpotifyPlaylist {
  id: string
  name: string
  external_urls: {
    spotify: string
  }
}

// Playlist-Konfiguration
const PLAYLIST_NAME = 'The BossHoss - Back to the Clubs Community Voting Top 15 Songs'
const OLD_PLAYLIST_NAME = '🤠 BossHoss - Back to the Boots (Community Top 15)'

// HIER DEIN COVER ALS BASE64 EINFÜGEN:
const DEFAULT_COVER_BASE64 = `
`

// Cover-Upload-Funktion
async function setPlaylistCover(accessToken: string, playlistId: string): Promise<boolean> {
  // Überspringe wenn kein Cover konfiguriert
  if (!DEFAULT_COVER_BASE64.trim()) {
    console.log('📷 No default cover configured')
    return false
  }
  
  try {
    console.log(`📸 Setting cover for playlist ${playlistId}`)
    
    const response = await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/images`, {
      method: 'PUT',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'image/jpeg'
      },
      body: DEFAULT_COVER_BASE64.trim()
    })
    
    if (response.ok) {
      console.log(`✅ Playlist cover set successfully`)
      return true
    } else {
      console.error(`❌ Failed to set playlist cover: ${response.status}`)
      const errorText = await response.text()
      console.error('Cover upload error:', errorText)
      return false
    }
  } catch (error) {
    console.error('Error setting playlist cover:', error)
    return false
  }
}

// Create or update BossHoss voting playlist for user
export async function POST(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions) as any
    
    if (!session?.user?.email || !session?.accessToken) {
      return NextResponse.json({ error: 'Not authenticated' }, { status: 401 })
    }
    
    console.log(`📱 Playlist request from user: ${session.user.email}`)
    
    // Get current top tracks from voting
    const topTracks = await getTopTracks(15)
    
    if (topTracks.length === 0) {
      return NextResponse.json({ error: 'No voting results yet' }, { status: 400 })
    }
    
    console.log(`📊 Found ${topTracks.length} top tracks for playlist`)
    
    // Get user's Spotify profile
    const profileResponse = await fetch('https://api.spotify.com/v1/me', {
      headers: {
        'Authorization': `Bearer ${session.accessToken}`
      }
    })
    
    if (!profileResponse.ok) {
      console.error('Spotify profile request failed:', profileResponse.status)
      return NextResponse.json({ error: 'Failed to get Spotify profile' }, { status: 400 })
    }
    
    const profile = await profileResponse.json()
    const userId = profile.id
    
    console.log(`🎵 Processing for Spotify user: ${userId}`)
    
    // Store tokens for automatic updates
    if (session.refreshToken) {
      try {
        await storeUserTokens(
          userId,
          session.user.email,
          session.accessToken,
          session.refreshToken,
          3600
        )
        console.log(`💾 Tokens stored for automatic updates: ${userId}`)
      } catch (error) {
        console.error('Error storing tokens:', error)
      }
    } else {
      console.warn(`⚠️ No refresh token available for ${userId}`)
    }
    
    // Check if playlist already exists
    const existingPlaylist = await findExistingPlaylist(session.accessToken, userId)
    
    let playlist: SpotifyPlaylist
    let isNewPlaylist = false
    
    if (existingPlaylist) {
      console.log(`🔄 Updating existing playlist: ${existingPlaylist.id}`)
      playlist = await updatePlaylist(session.accessToken, existingPlaylist.id, topTracks)
    } else {
      console.log('✨ Creating new playlist')
      playlist = await createNewPlaylist(session.accessToken, userId, topTracks)
      isNewPlaylist = true
    }
    
    // WICHTIG: Cover setzen
    console.log(`🖼️ Setting playlist cover...`)
    const coverSet = await setPlaylistCover(session.accessToken, playlist.id)
    
    if (coverSet) {
      console.log(`✅ Cover successfully set for playlist ${playlist.id}`)
    } else {
      console.log(`⚠️ Cover not set for playlist ${playlist.id}`)
    }
    
    return NextResponse.json({
      success: true,
      playlist: {
        id: playlist.id,
        name: playlist.name,
        url: playlist.external_urls.spotify
      },
      tracksCount: topTracks.length,
      message: existingPlaylist ? 'Playlist updated!' : 'Playlist created!',
      automaticUpdates: !!session.refreshToken,
      coverSet,
      isNewPlaylist
    })
    
  } catch (error) {
    console.error('Error in playlist creation:', error)
    return NextResponse.json({ 
      error: 'Internal server error', 
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 })
  }
}

// Get user's playlist status
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions) as any
    
    if (!session?.accessToken) {
      return NextResponse.json({ hasPlaylist: false })
    }
    
    const profileResponse = await fetch('https://api.spotify.com/v1/me', {
      headers: {
        'Authorization': `Bearer ${session.accessToken}`
      }
    })
    
    if (!profileResponse.ok) {
      return NextResponse.json({ hasPlaylist: false })
    }
    
    const profile = await profileResponse.json()
    const userId = profile.id
    
    const existingPlaylist = await findExistingPlaylist(session.accessToken, userId)
    
    if (existingPlaylist) {
      return NextResponse.json({
        hasPlaylist: true,
        playlist: {
          id: existingPlaylist.id,
          name: existingPlaylist.name,
          url: existingPlaylist.external_urls.spotify
        }
      })
    }
    
    return NextResponse.json({ hasPlaylist: false })
    
  } catch (error) {
    console.error('Error checking playlist status:', error)
    return NextResponse.json({ hasPlaylist: false })
  }
}

// Helper: Find existing playlist (sucht nach altem UND neuem Namen)
async function findExistingPlaylist(accessToken: string, userId: string): Promise<SpotifyPlaylist | null> {
  try {
    const playlistsResponse = await fetch(`https://api.spotify.com/v1/users/${userId}/playlists?limit=50`, {
      headers: {
        'Authorization': `Bearer ${accessToken}`
      }
    })
    
    if (!playlistsResponse.ok) return null
    
    const playlistsData = await playlistsResponse.json()
    
    // Suche nach neuem Namen
    let existingPlaylist = playlistsData.items?.find((playlist: SpotifyPlaylist) => 
      playlist.name === PLAYLIST_NAME
    )
    
    // Falls nicht gefunden, suche nach altem Namen für Migration
    if (!existingPlaylist) {
      existingPlaylist = playlistsData.items?.find((playlist: SpotifyPlaylist) => 
        playlist.name === OLD_PLAYLIST_NAME
      )
      
      // Wenn alte Playlist gefunden, umbenennen
      if (existingPlaylist) {
        console.log(`🔄 Migrating playlist from "${OLD_PLAYLIST_NAME}" to "${PLAYLIST_NAME}"`)
        await fetch(`https://api.spotify.com/v1/playlists/${existingPlaylist.id}`, {
          method: 'PUT',
          headers: {
            'Authorization': `Bearer ${accessToken}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            name: PLAYLIST_NAME
          })
        })
        
        // Update lokales Objekt mit neuem Namen
        existingPlaylist.name = PLAYLIST_NAME
      }
    }
    
    return existingPlaylist || null
  } catch (error) {
    console.error('Error finding existing playlist:', error)
    return null
  }
}

// Helper: Create new playlist
async function createNewPlaylist(accessToken: string, userId: string, topTracks: Array<{
  trackId: string
  totalPoints: number
  totalVotes: number
  trackName: string
  artistName: string
  albumName: string
  rank: number
}>): Promise<SpotifyPlaylist> {
  
  const description = `Die beliebtesten The BossHoss Songs basierend auf Community Voting für die Back to the Clubs Tour 2025. Wird täglich automatisch aktualisiert! 🎸 Erstellt: ${new Date().toLocaleDateString('de-DE')}`
  
  const createResponse = await fetch(`https://api.spotify.com/v1/users/${userId}/playlists`, {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${accessToken}`,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      name: PLAYLIST_NAME,
      description: description,
      public: false,
      collaborative: false
    })
  })
  
  if (!createResponse.ok) {
    const errorText = await createResponse.text()
    console.error('Failed to create playlist:', createResponse.status, errorText)
    throw new Error(`Failed to create playlist: ${createResponse.status}`)
  }
  
  const playlist = await createResponse.json()
  
  // Add tracks to playlist
  await addTracksToPlaylist(accessToken, playlist.id, topTracks)
  
  return playlist
}

// Helper: Update existing playlist
async function updatePlaylist(accessToken: string, playlistId: string, topTracks: Array<{
  trackId: string
  totalPoints: number
  totalVotes: number
  trackName: string
  artistName: string
  albumName: string
  rank: number
}>): Promise<SpotifyPlaylist> {
  
  const description = `Die beliebtesten The BossHoss Songs basierend auf Community Voting für die Back to the Clubs Tour 2025. Wird täglich automatisch aktualisiert! 🎸 Letztes Update: ${new Date().toLocaleDateString('de-DE', {
    day: '2-digit',
    month: '2-digit', 
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  })}`
  
  // Update playlist name and description
  await fetch(`https://api.spotify.com/v1/playlists/${playlistId}`, {
    method: 'PUT',
    headers: {
      'Authorization': `Bearer ${accessToken}`,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      name: PLAYLIST_NAME,
      description: description
    })
  })
  
  // Clear existing tracks
  await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks`, {
    method: 'PUT',
    headers: {
      'Authorization': `Bearer ${accessToken}`,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      uris: []
    })
  })
  
  // Add new tracks
  await addTracksToPlaylist(accessToken, playlistId, topTracks)
  
  // Get updated playlist info
  const playlistResponse = await fetch(`https://api.spotify.com/v1/playlists/${playlistId}`, {
    headers: {
      'Authorization': `Bearer ${accessToken}`
    }
  })
  
  return await playlistResponse.json()
}

// Helper: Add tracks to playlist
async function addTracksToPlaylist(accessToken: string, playlistId: string, topTracks: Array<{
  trackId: string
  totalPoints: number
  totalVotes: number
  trackName: string
  artistName: string
  albumName: string
  rank: number
}>): Promise<void> {
  const trackUris = topTracks.map(track => `spotify:track:${track.trackId}`)
  
  const batchSize = 50
  for (let i = 0; i < trackUris.length; i += batchSize) {
    const batch = trackUris.slice(i, i + batchSize)
    
    await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${accessToken}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        uris: batch
      })
    })
  }
}